const Icons = {
    system: SystemIcon,
    dark: DarkIcon,
    search: SearchIcon,
    light: LightIcon,
    star: StarIcon,
    up: UpIcon,
    tg: TelegramIcon,
    plan: PlanIcon,
    pen: PenIcon,
    menu: MenuIcon,
    link: LinkIcon,
    code: CodeIcon,
    cancel: CancelIcon,
    arrowLeft: ArrowLeftIcon,
    arrowRight: ArrowRightIcon,
    attachment: AttachmentIcon,
} as const;

export type IconName = keyof typeof Icons;

interface IconProps extends Omit<SVGProps<SVGSVGElement>, 'name'> {
    name: IconName;
    size?: number;
}

export default function Icon({
                                 name,
                                 size = 24,
                                 ...props
                             }: IconProps) {
    const IconComponent = Icons[name];

    return (
        <IconComponent
            width={size}
            height={size}
            {...props}
        />
    );
}
export { Icons };